mapper.select:
SELECT BOARD_NO , BOARD_TITLE , BOARD_CONTENT , BOARD_WRITER , TO_CHAR(WRITE_DATE, 'YYYY-MM-DD HH24:MI:SS') WRITE_DATE
FROM TB_BOARD
WHERE BOARD_NO = ${no}

SELECT BOARD_PW 
FROM TB_BOARD
WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE TB_BOARD
SET BOARD_DEL_FL = 'Y'
WHERE BOARD_NO = #{boardNo}

UPDATE TB_BOARD SET
BOARD_TITLE = #{boardTitle},
BOARD_CONTENT = #{boardContent},
BOARD_PW = #{boardPw},
BOARD_WRITER = #{boardWriter}
WHERE BOARD_NO = #{boardNo}
