mapper.select:
SELECT BOARD_NO , BOARD_TITLE , BOARD_CONTENT , READ_COUNT , TO_CHAR(BOARD_WRITE_DATE, 'YYYY"년" MM"월" DD"일" HH24:MI:SS') BOARD_WRITE_DATE, TO_CHAR(BOARD_UPDATE_DATE, 'YYYY"년" MM"월" DD"일" HH24:MI:SS') BOARD_UPDATE_DATE, MEMBER_NICKNAME , PROFILE_IMG , MEMBER_NO , (SELECT COUNT(*) FROM "BOARD_LIKE" L WHERE L.BOARD_NO = B.BOARD_NO ) LIKE_COUNT
FROM "BOARD" B JOIN "MEMBER" USING (MEMBER_NO)
WHERE BOARD_DEL_FL ='N' AND BOARD_CODE = #{boardCode} AND BOARD_NO = #{boardNo}

SELECT COUNT(*) FROM BOARD_LIKE
WHERE BOARD_NO  = #{boardNo}
AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE FROM BOARD_LIKE
WHERE MEMBER_NO = #{memberNo}
AND BOARD_NO = #{boardNo}

mapper.update:
UPDATE BOARD SET
READ_COUNT = READ_COUNT + 1
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT * FROM BOARD_IMG
WHERE BOARD_NO = #{boardNo}
ORDER BY IMG_ORDER
